let wrapper;const sleep=a=>new Promise(b=>setTimeout(b,a));async function writingAll(a,b){wrapper=document.querySelector("["+b+"]");for(const c=document.getElementsByClassName(a);wrapper;)for(i=0;i<c.length;i++){const a=c[i].textContent;await write(a),await sleep(500),await erase(),await sleep(500)}}async function write(a){for(let b=0;b<a.length;){await sleep(80),b++,wrapper.innerHTML=a.substring(0,b)}}async function erase(){for(;wrapper.textContent.length;){await sleep(80),wrapper.textContent=wrapper.textContent.substring(0,wrapper.textContent.length-2)}}writingAll("item","data-text");